import{_ as t,c as i,o as r,ae as a}from"./chunks/framework.oxQ2x61N.js";const f=JSON.parse('{"title":"Présentation de relatix","description":"","frontmatter":{},"headers":[],"relativePath":"fr/intro.md","filePath":"fr/intro.md"}'),s={name:"fr/intro.md"};function l(n,e,o,c,u,d){return r(),i("div",null,e[0]||(e[0]=[a('<h1 id="presentation-de-relatix" tabindex="-1">Présentation de relatix <a class="header-anchor" href="#presentation-de-relatix" aria-label="Permalink to &quot;Présentation de relatix&quot;">​</a></h1><p><strong><code>relatix</code> facilite la création et la manipulation de données relationnelles en TypeScript</strong>.</p><p>Créez, reliez et interrogez vos tables en toute simplicité, sans sacrifier la sécurité de typage. Sa syntaxe claire et intuitive permet de définir des tables interconnectées et de les peupler de données cohérentes grâce à un typage fort de l&#39;état initial.</p><h2 id="pourquoi-choisir-relatix" tabindex="-1">Pourquoi choisir relatix ? <a class="header-anchor" href="#pourquoi-choisir-relatix" aria-label="Permalink to &quot;Pourquoi choisir relatix ?&quot;">​</a></h2><p>✅ <strong>Modélisation déclarative</strong></p><ul><li>Définissez vos tables et leurs relations en quelques lignes grâce aux Typers (Text, Number, SelfRef, Ref).</li><li>Les références sont validées à la compilation</li></ul><p>✅ <strong>Typage fort et automatique</strong></p><ul><li>Les schémas de tables, les refs, les sélecteurs et les commits sont tous générés par inférence.</li><li>Zéro any caché : vos IDE et votre CI détectent les incohérences avant même l’exécution.</li></ul><p>✅ <strong>Chaînage fluide du workflow</strong></p><ul><li>Construisez vos tables pas à pas : .addTables() accepte des refs vers les tables déjà définies.</li><li>.populate() injecte facilement des données cohérentes et vérifiées.</li></ul><p>✅ <strong>Utilitaires « batteries incluses »</strong></p><ul><li>select, deepSelect, create, commit… tout est prêt pour lire, muter ou naviguer dans vos données.</li><li>Personnalisez la génération des id et label avec TableOptions pour un débogage plus lisible.</li></ul><p>✅ <strong>Performances et maintenabilité</strong></p><ul><li>Sélecteurs mémoïsés : recalcul unique quand la table cible change.</li></ul>',14)]))}const m=t(s,[["render",l]]);export{f as __pageData,m as default};
